mapper.select:
SELECT member_no, originalfilename, systemfilename, filesize, bio
FROM profilePhoto
WHERE member_no = #{no}

mapper.update:
UPDATE profilePhoto
SET originalfilename = #{profile.originalFileName},
    systemfilename = #{profile.systemFileName},
    filesize = #{profile.fileSize}
WHERE member_no = #{no}

UPDATE profilephoto
SET bio = #{bio}
WHERE member_no = #{no}
